var eventHub = require('../../clients/server/eventClient.js').getClientHub('zzo.eventhub.jit.su?token=ehrox');

eventHub.on('eventHubReady', function() { 
    console.log('Sending click event');
    eventHub.emit("click", { data: 'some data' }, function response(error, data) {
        console.log("In callback got:");
        console.log(data);
        console.log("Error:");
        console.log(error);
        process.exit(0);
    });
});

